$:/plugins/kookma/utility/styles/code.text:
figure.kk-utility-fig {
	/* for future use*/
	margin-left:0;
	margin-right:0;
}

/*figcaption structure */
figure.kk-utility-fig figcaption {
	padding: 4.5px 7.5px 7.5px 7.5px;
	border-top-left-radius:  3px;
	border-top-right-radius: 3px;
	margin-bottom: -3px;
}

/* figcaption skin: colors */
figure.kk-utility-fig figcaption{
	opacity: 0.7;
	background-color:black;
	color:white;
}

/* correct the style of codeblock pre element  */
figure.kk-utility-fig pre{
	margin-top:0;
	/*	used to have inner top border straight */
	/*	border-top-left-radius:  0;  
	border-top-right-radius: 0; */
}


/* the code block and copy-to-clipboard button */
.kk-utility-code{
	position:  relative;
}
/* pretty display the empty code block */
.kk-utility-code pre code{
	display: block;
	min-height:30px; 
}

.kk-utility-copy-btn{
	position: absolute;
	top: 3px;
	right: 5px;
	opacity: 0.3;
/*	fill:currentColor;*/
}

.tc-btn-invisible.kk-utility-copy-btn svg{
width: 1.2em;
height: 1.2em;
}

.kk-utility-copy-btn:hover{
    transition: opacity 200ms ease-in-out;
    opacity: 1;
}

$:/plugins/kookma/utility/styles/reveal-.text:
details.kk-utility-details summary {
	opacity:0.2;
	font-size:0.9em;
	display: inline;
	cursor: pointer;
	padding: 10px;
	transition: 0.3s;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
}
details.kk-utility-details summary:hover,
details.kk-utility-details[open] > summary {
	opacity:1;
}

/* hide in preview pane */
.tc-tiddler-preview-preview .kk-utility-details{
	display: none; 
}


/* used in $:/plugins/kookma/utility/ui/ControlPanel/Settings */
.kk-utility-label{
	width:26em;
	display: block;
	margin-top:5px;
}

.kk-ut-txt{
	min-width:50%;
}

$:/plugins/kookma/utility/styles/tiddler.text:
/* tiddler size table */

.kk-utility-tiddlersize{
	margin:0.5em;
}
.kk-utility-tiddlersize tr,
.kk-utility-tiddlersize th,
.kk-utility-tiddlersize td {
	padding:0 0.25em;
	margin:0; 
	text-align:right;
}

/* tiddler size button and dropdown in Edit mode */
.kk-utility-tiddlersize-btn{
	float:left;
	margin-top:-1.25em;
}
.kk-utility-tiddlersize-dropdwon{
	padding:0;
	font-size:90%;
	min-width:unset;
}

$:/plugins/kookma/utility/styles/tw-vers.text:
