<%if [{$:/config/Stylesheet/Visibility/$:/plugins/kookma/utility/styles/tw-version}match[show]] %>
/* TW-version added to site title */
.tc-site-title:before {
  content:"TW <<version>>";
  position:absolute;
  margin-top:-1.9em;
  color:<<color sidebar-foreground>>;
  font-size:13px;
}
<%endif%>

$:/plugins/kookma/utility/styles/wikitex.text:
.doc-example {
	margin: 1em 0;
	padding: 0.8em 0;
}
.doc-example:hover {
	background-color: <<colour code-background>>;
}
.doc-example ul {
	margin-bottom: 0;
	padding-bottom: 0;
	margin-top: 0.2em;
}

.doc-example input[type=search] { 
  width: 95%;
}
.doc-example pre:first-child {
	margin-top: 0;
}
